customElements.define("my-counter",class extends HTMLElement{constructor(){super().attachShadow({mode:"open"}).innerHTML="<style>span{font-size:200%;width:4rem;display:inline-block;text-align:center}button{font-size:200%;width:4rem;height:4rem;border:none;border-radius:10px;background:seagreen;color:white}</style><button onclick=this.getRootNode().host.count-- >-</button><span>0</span><button onclick=this.getRootNode().host.count++>+</button>"}set count(t){this.shadowRoot.querySelector("span").innerHTML=t}get count(){return~~this.shadowRoot.querySelector("span").innerHTML}});